import React, { Component } from 'react';
import Movie from './components/apiMovies'
import './App.css';

class App extends Component {

  constructor(props) {
    super(props)
    this.state = { valueFilter: "" }
    this.filterGenreTitle = this.filterGenreTitle.bind(this);
    this.filterGenreTitleSubmit = this.filterGenreTitleSubmit.bind(this);
  }


  filterGenreTitle(event) {
    this.state.valueFilter = event.target.value
  }

  filterGenreTitleSubmit(event){
    this.state.valueFilter = event.target.value
    event.preventDefault();
    alert("oi" + this.state.valueFilter )
  }

  render() {

    return (
      <div>
        <div className="div-filter">

          <form onSubmit={this.filterGenreTitleSubmit}>

            Filter for Genre or Title:
              <input
              class="div-filter-input"
              type="text"
              onChange={this.filterGenreTitle}
            />

            <button type="submit">
              Filter
           </button>
          </form>
        </div>
        <Movie filter={this.state.valueFilter} />
      </div>
    );

  }


}

export default App
