import React, { Component } from 'react'
import { Link, Route, Switch } from 'react-router-dom'
import Details from '../Pages/Home/details'
import routesConfig from '../routerconfig';

class Movie extends Component {

    render() {
        return (
            <div className="div-movie_complete">
                <div>

                    <span>
                        <b>{this.props.nameMovie}</b>
                    </span>

                </div>
                <div>
                    <img src={"http://image.tmdb.org/t/p/w185/" + this.props.poster} alt={this.props.nameAlt} />
                </div>
                <div>
                    <span><b>Release Date:</b> {this.props.dateRelease} </span>
                    <span><b>Genre:</b> {
                        this.props.genres.map((genre) => {
                            return this.props.genresArray[genre] + " / "
                        })
                    } </span>
                </div>
                <div id="div-overview">
                    <span>{this.props.resumo}</span>
                </div>
                <div id="div-overview">
                    <Link to="/details" onClick={this.props.trocar}>
                        <span>More Details...</span>
                    </Link>

                </div>
                {routesConfig.map((value, key) => {
                    return <Switch>
                        <Route
                            key={key}
                            path={value.path}
                            render={(props) => <Details {...props} name="Joao" id={this.props.id} />}></Route>
                    </Switch>
                })}
            </div>


        )
    }
}

export default Movie
