import React, { Component } from 'react'
import { Link, Route, Switch } from 'react-router-dom'

class Movie extends Component {

    render() {
        return (
            <div>
                <div>
                    <Link to="/details" target="_blank" onClick={this.props.trocar}
                    >
                        <span>
                            <b>{this.props.nameMovie}</b>
                        </span>
                    </Link>
                </div>
                <div>
                    <img src={"http://image.tmdb.org/t/p/w185/" + data.poster_path} alt={data.title} />
                </div>
                <div>
                    <span><b>Release Date:</b> {data.release_date} </span>
                    <span><b>Genre:</b> {
                        data.genre_ids.map((genre) => {
                            return arrayGenres[genre] + " / "
                        })
                    } </span>
                </div>
                <div id="div-overview">
                    <span>{data.overview}</span>
                </div>
            </div>


        )
    }
}

export default Movie
