import React, { Component } from 'react'
import axios from 'axios'

class MovieDetails extends Component {

    constructor(props){
        super();
        this.state = {
            idMovie: ''
        }
    }

    componentDidUpdate = () => {

        if(this.state.viewDetailsMovie) {
            axios.get('http://127.0.0.1:8000/api/movies/details/' + this.state.idMovie).then((resp => {
                console.log(resp)
            }))
        }

    }

    render() {
        return (
            <div className="div-movie-details">
                id: {this.props.idMovie}
            </div>
        );
    }

}

export default MovieDetails
