import React, { Component } from 'react';
import Movie from './components/apiMovies'
import './App.css';

class App extends Component {

  state = {
    valueFilter : ''
  }

  filterGenreTitle = (event) => {
    this.setState({valueFilter: event.target.value})
  }

  render() {

    return (
      <div>
        <div className="div-filter">
          <input 
            class="div-filter-input" 
            type="text" placeholder="Enter the name or genre of the movie..." 
            valueInput={this.state.valueFilter} changeValueInput={this.filterGenreTitle}
            />
          <button className="button-filter" >
            Filter
          </button>
        </div>
        <Movie configuration="popular?" filter="a" />
      </div>
    );

  }


}

export default App
