import React, { Component } from 'react';
import axios from 'axios';
class Movie extends Component {

    state = {
        movie: [],
        genres: [],
        urlBase: "https://api.themoviedb.org/3/movie/",
        api_key: "4ec327e462149c3710d63be84b81cf4f",
        method: this.props.configuration
    }

    componentDidMount = () => {
        this.getMovies()
        this.getGenres()
    }

    getMovies = () => {
        const request = axios.get(this.state.urlBase + this.state.method + "api_key=" + this.state.api_key)
        request.then((resp) => {
            this.setState({ movie: resp.data.results });
            console.log(this.state.movie)

        })
    }

    getGenres = () => {
        const request = axios.get("https://api.themoviedb.org/3/genre/movie/list?api_key=" + this.state.api_key)
        request.then((respGenres) => {
            this.setState({ genres: respGenres.data.genres });
            //console.log(this.state.genres)

        })
    }

    moutArrayGenres = () => {
        let arrayGenres = []
        this.state.genres.forEach(element => {
            arrayGenres[element.id] = element.name
        });

        return arrayGenres
    }

    render() {

        const arrayGenres = this.moutArrayGenres()

        return (
            <div className="">
                {
                    this.state.movie.map((data) => {
                        return <div className="div-movies">
                            <div>
                                <span>{data.title}</span>
                            </div>
                            <div>
                                <img src={"http://image.tmdb.org/t/p/w185/" + data.poster_path} alt={data.title} />
                            </div>
                            <div>
                                <span>Release date: {data.release_date} </span>
                                <span>Genre: {
                                    data.genre_ids.map((genre) => {
                                        return arrayGenres[genre] +  " / " 
                                    })
                                } </span>
                            </div>
                            <div>
                                <span>{data.overview}</span>
                            </div>
                        </div>
                    })
                }
            </div>
        );
    }

}

export default Movie;
