import React, { Component } from 'react'
import axios from 'axios'

class MovieDetails extends Component {

    constructor(props){
        super();
        this.state = {
            movideDetails: []
        }
    }

    componentDidMount(){
        if (this.props.viewDetailsMovie) {
            axios.get('http://127.0.0.1:8000/api/movies/details/' + this.props.idMovie).then((resp => {
                this.setState({movideDetails : resp.data})
            }))
        }
    }
    render() {
        const {movieDetails} = this.state.movideDetails
        console.log(movieDetails)
        return (
            <div className="div-movie-details">
               {/* {
                   movieDetails.map((resp => {
                       return <div>
                           {(resp.adult) ? 'Yes' : 'No'}
                       </div>
                   }))
               } */}
            </div>
        );
    }

}

export default MovieDetails
