import React, { Component } from 'react';
import Movie from './components/apiMovies'
import './App.css';

class App extends Component {

  filterGenreTitle = () => {
    alert("Opa")
  }

  render() {

    return (
      <div>
        <div className="div-filter">
          <input class="div-filter-input" type="text" placeholder="Enter the name or genre of the movie..." />
          <button className="button-filter" onClick={this.filterGenreTitle}>
            Filter
          </button>
        </div>
        <Movie configuration="popular?" filter="a" />
      </div>
    );

  }


}

export default App
