import React, { Component } from 'react';
import Movie from './components/apiMovies'
import './App.css';

class App extends Component {

  constructor(props) {
    super(props)
    this.state = { valueFilter: "" }
    this.filterGenreTitle = this.filterGenreTitle.bind(this);
  }

  componentDidMount = () => {
    this.filterGenreTitle()
  }

  async filterGenreTitle(event) {

    this.setState({ valueFilter: event.target.value })
    alert(event.target.value)
  }

  render() {

    return (
      <div>
        <div className="div-filter">
          <input
            class="div-filter-input"
            type="text" placeholder="Enter the name or genre of the movie..."
            onChange={this.filterGenreTitle}
          />
        </div>
        <Movie configuration="popular?" filter={this.state.valueFilter} />
      </div>
    );

  }


}

export default App
