import React, { Component } from 'react'
import axios from 'axios'

class MovieDetails extends Component {

    constructor(props){
        super();
        this.state = {
            movideDetails: []
        }
    }

    componentDidUpdate(){
        if (this.props.viewDetailsMovie) {
            axios.get('http://127.0.0.1:8000/api/movies/details/' + this.props.idMovie).then((resp => {
                this.setState({movideDetails : resp.data})
            }))
        }
    }
    render() {

        console.log(this.state.movideDetails)

        return (
            <div className="div-movie-details">
               {/* {
                   this.state.movideDetails.map((resp => {
                       return <div>
                           {(resp.adult) ? 'Yes' : 'No'}
                       </div>
                   }))
               } */}
            </div>
        );
    }

}

export default MovieDetails
