import React, { Component } from 'react';
import axios from 'axios';


class Movie extends Component {

    state = {
        movie: [],
        urlBase: "https://api.themoviedb.org/3/movie/",
        api_key: "4ec327e462149c3710d63be84b81cf4f",
        method: this.props.configuration
    }

    componentDidMount() {

        axios.get(this.state.urlBase + this.state.method + "api_key=" + this.state.api_key).then((resp) => {
            const movies = resp.data;
            this.setState({movies: movies})
            console.log(movies);
        })
    }

    render() {
        return (
            <div className="div-movies">
                
            </div>
        );
    }

}

export default Movie;
