import React, { Component } from 'react';
import Movie from './components/apiMovies'
import './App.css';

class App extends Component {

  constructor(props) {
    super(props)
    this.state = { valueFilter: "" }
    this.filterGenreTitle = this.filterGenreTitle.bind(this);
  }


  filterGenreTitle(event) {

    this.state.valueFilter = event.target.value
  }

  render() {

    return (
      <div>
        <div className="div-filter">
          <input
            class="div-filter-input"
            type="text" placeholder="Enter the name or genre of the movie..."
            onChange={this.filterGenreTitle}
          />

          <form onSubmit={this.handleSubmit}>
            <label>
              Nome:
              <input type="text" value={this.state.value} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Enviar" />
          </form>
        </div>
        <Movie filter={this.state.valueFilter} />
      </div>
    );

  }


}

export default App
