import React, { Component } from 'react';
import axios from 'axios';


class Movie extends Component {

    state = {
        movie: [],
        urlBase: "https://api.themoviedb.org/3/movie/",
        api_key: "4ec327e462149c3710d63be84b81cf4f",
        method: this.props.configuration
    }

    componentDidMount = () => {
        this.getMovies()
    }

    getMovies = () => {
        const request = axios.get(this.state.urlBase + this.state.method + "api_key=" + this.state.api_key)
        request.then((resp) => {
            this.setState({ movie: resp.data.results });
            console.log(this.state.movie)

        })
    }

    render() {

        return (
            <div>
                {
                    this.state.movie.map((data) => {
                        return <div className="div-movies">
                            <span>{data.backdrop_path} </span>
                        </div>
                    })
                }
            </div>
        );
    }

}

export default Movie;
