import React, { Component } from 'react';
import axios from 'axios';

class Movie extends Component {

    constructor(props) {
        this.state = {
            movie: [],
            genres: [],
            api_key: "4ec327e462149c3710d63be84b81cf4f",
            valueFilter: ''
        }
    }
    

    componentDidMount = () => {
        this.getMovies()
        this.getGenres()
    }

    componentDidUpdate() {
        console.log("Updated!");
        this.filterGenreTitle()
    }

    getMovies = () => {

        const request = axios.get("https://api.themoviedb.org/3/movie/popular?api_key=" + this.state.api_key)
        request.then((resp) => {
            this.setState({ movie: resp.data.results });
            //console.log(this.state.movie.sort())

        })
    }

    getGenres = () => {
        const request = axios.get("https://api.themoviedb.org/3/genre/movie/list?api_key=" + this.state.api_key)
        request.then((respGenres) => {
            this.setState({ genres: respGenres.data.genres });
            //console.log(this.state.genres)

        })
    }

    mountArrayGenres = () => {
        let arrayGenres = []
        this.state.genres.forEach(element => {
            arrayGenres[element.id] = element.name
        });

        return arrayGenres
    }

    filterGenreTitle = (event) => {
        if(event.target !== undefined) {

            this.setState({ valueFilter: event.target.value });

            if (this.state.valueFilter.indexOf(' ') >= 0) {
                this.setState({ valueFilter: this.state.valueFilter.replace(' ', '+') });
    
                // componentDidUpdate = () => {
                //     const request = axios.get("https://api.themoviedb.org/3/movie/popular?api_key=" + this.state.api_key + "&query=" + this.state.valueFilter)
                //     request.then((resp) => {
                //         this.setState({ movie: resp.data.results });
                //         console.log(this.state.movie.sort())
    
                //     })
                // }
            }
    
            console.log(this.state.valueFilter)
        }
    }



    render() {

        const arrayGenres = this.mountArrayGenres()
        return (
            <div>
                <div className="div-filter">

                    Filter for Genre or Title:
                        <input
                        class="div-filter-input"
                        type="text"
                        onChange={this.filterGenreTitle}
                    />

                </div>
                {
                    this.state.movie.map((data) => {
                        return <div className="div-movies">
                            <div>
                                <span><b>{data.title}</b></span>
                            </div>
                            <div>
                                <img src={"http://image.tmdb.org/t/p/w185/" + data.poster_path} alt={data.title} />
                            </div>
                            <div>
                                <span><b>Release Date:</b> {data.release_date} </span>
                                <span><b>Genre:</b> {
                                    data.genre_ids.map((genre) => {
                                        return arrayGenres[genre] + " / "
                                    })
                                } </span>
                            </div>
                            <div id="div-overview">
                                <span>{data.overview}</span>
                            </div>
                        </div>
                    })
                }
            </div>
        );
    }

}

export default Movie;
